import React, {useState, useEffect} from 'react';
import webApi from '../utils/WebApi';
import Grid from './Grid';
import { Button,Modal, Box, FormControl,InputLabel, OutlinedInput } from '@mui/material';
import ContactForm from './ContactForm';

function ContactsList(){
  const [contacts, setContacts] = useState([]);
  useEffect(() => {
    async function fetchData(){
    try {
        const response = await webApi.get()
        setContacts(response.data)
      } catch (error) {
        console.error('Error fetching contacts:', error)
      }}

      fetchData();
  }, [])
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

    async function addData(contact){
    try{
        const response = await webApi.post('/',contact);
        alert(response.data);
    } catch (error) {
        console.error('Error adding contact:', error)
      }
}
  const handleSubmit= (contact)=>{
    addData(contact);
    setOpen(false);

  }
  const handleCancel=(e)=>{
    e.preventDefault();
    setOpen(false);
  }
  return(<div>
    <Button onClick={handleOpen}>Add Contact</Button>
    <Grid contacts={contacts} open={open} handleClose={handleClose} onSubmit={handleSubmit} onCancel={handleCancel} />
    <ContactForm open={open} handleClose={handleClose} onSubmit={handleSubmit} onCancel={handleCancel}/>
        
    </div>);
}

export default ContactsList;
