import { Button, FormControl, FormGroup, Input, InputLabel, Typography } from '@mui/material';
import React from 'react';
import './EditContact.css';
import { useState } from 'react';
import { Navigate } from 'react-router-dom';

const initialValue = {
  fullname: "",
  number: "",
  emailAddress: ""
}

const EditContact = () => {
  const [contact, setContact] = useState(initialValue);
  const { fullName, number, emailAddress } = contact;
  const onValueChange = (e) => {
    console.log(e.target.value);
    setContact({...contact, [e.target.name]: e.target.value});
  };

  const editContactDetails = () => {
    Navigate('/');
  }

  return (
    <FormGroup className='form-grp'>
      <Typography variant='h4' className='typography'>Edit Contact Details</Typography>
      <FormControl className='form-ctrl'>
        <InputLabel htmlFor="name-input">Contact Name</InputLabel>
        <Input 
        onChange={(e) => onValueChange(e)}
        name='fullName'
        value={fullName}
        id='name-input' 
        />
      </FormControl>
      <FormControl className='form-ctrl'>
        <InputLabel htmlFor="number-input">Contact Number</InputLabel>
        <Input 
        onChange={(e) => onValueChange(e)}
        name='number'
        value={number}
        id='number-input' 
        />
      </FormControl>
      <FormControl className='form-ctrl'>
        <InputLabel htmlFor="email-input">Email Address</InputLabel>
        <Input 
        onChange={(e) => onValueChange(e)}
        name='emailAddress'
        value={emailAddress}
        id='email-input' 
        />
      </FormControl>
      <FormControl className='form-ctrl'>
        <Button variant='contained' color='primary' onClick={() => editContactDetails()}>
          Edit Contact
        </Button>
      </FormControl>
    </FormGroup>
  )
}

export default EditContact
