import * as React from 'react';
import IconButton from '@mui/material/IconButton';
import Box from '@mui/material/Box';
import { DataGrid } from '@mui/x-data-grid';
import Checkbox from "@mui/material/Checkbox";
import StarBorder from "@mui/icons-material/StarBorder";
import Star from "@mui/icons-material/Star";
import DeleteIcon from "@mui/icons-material/Delete";
import EditNoteIcon from '@mui/icons-material/EditNote';
import ResponsiveDialog from './Dialog';

const columns = [
  {
    field: 'isStarred', 
    headerName: 'Favorite', 
    width: 90,
    renderCell: () => (
      <Checkbox icon={<StarBorder />} checkedIcon={<Star />} />)
  },
  { field: 'id', headerName: 'ID', width: 90 },
  {
    field: 'firstName',
    headerName: 'First name',
    width: 150,
    editable: false,
  },
  {
    field: 'lastName',
    headerName: 'Last name',
    width: 150,
    editable: false,
  },
  {
    field: 'emailAddress',
    headerName: 'Email Address',
    width: 110,
    editable: false,
  },
  {
    field: 'mobileNumber',
    headerName: 'Mobile Number',
    sortable: true,
    width: 160
  },
  {
    field: 'actions', 
    headerName: 'Actions', 
    width: 180,
    renderCell: () => (
      <div>
        <IconButton aria-label="edit">
          <EditNoteIcon />
        </IconButton>
        <IconButton aria-label="delete">
          <DeleteIcon />
        </IconButton>
      </div>)
  }
];

const rows = [
  { id: 1, lastName: 'Snow', firstName: 'Jon', emailAddress: 35 },
  { id: 2, lastName: 'Lannister', firstName: 'Cersei', emailAddress: 42 },
  { id: 3, lastName: 'Lannister', firstName: 'Jaime', emailAddress: 45 },
  { id: 4, lastName: 'Stark', firstName: 'Arya', emailAddress: 16 },
  { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', emailAddress: null },
  { id: 6, lastName: 'Melisandre', firstName: null, emailAddress: 150 },
  { id: 7, lastName: 'Clifford', firstName: 'Ferrara', emailAddress: 44 },
  { id: 8, lastName: 'Frances', firstName: 'Rossini', emailAddress: 36 },
  { id: 9, lastName: 'Roxie', firstName: 'Harvey', emailAddress: 65 },
];


function Grid() {
  return (
    <div className="App">
      <Box sx={{ height: 400, width: '100%' }}>
      <DataGrid
        rows={rows}
        columns={columns}
        initialState={{
          pagination: {
            paginationModel: {
              pageSize: 5,
            },
          },
        }}
        pageSizeOptions={[5]}
      />
    </Box>
    <ResponsiveDialog message="Are you sure you want to delete this contact?" open={true}></ResponsiveDialog>
    </div>
  );
}

export default Grid;
