import React,{useState} from 'react';
import { DataGrid } from '@mui/x-data-grid';
import {IconButton,Box,Checkbox} from '@mui/material';
import {StarBorder,Star,Delete, EditNote} from "@mui/icons-material";

const Grid=({contacts,onDelete,onEdit}) =>{
const [selectedID, setSelectedID]=useState('');
const columns = [
  {
    field: 'isStarred', 
    headerName: 'Favorite', 
    width: 90,
    renderCell: (params) => (
      <Checkbox icon={<StarBorder />} checkedIcon={<Star />} checked={params.row.isStarred} />)
  },
  { field: 'id', headerName: 'ID', width: 90 },
  {
    field: 'firstName',
    headerName: 'First name',
    width: 150,
    editable: false,
  },
  {
    field: 'lastName',
    headerName: 'Last name',
    width: 150,
    editable: false,
  },
  {
    field: 'emailAddress',
    headerName: 'Email Address',
    width: 110,
    editable: false,
  },
  {
    field: 'mobileNumber',
    headerName: 'Mobile Number',
    sortable: true,
    width: 160,
    editable: false,
  },
  {
    field: 'actions', 
    headerName: 'Actions', 
    width: 180,
    renderCell: (params) => (
      <div>
        <IconButton aria-label="edit" onClick={(e)=>{handleEdit(e, params.row);}}>
          <EditNote />
        </IconButton>
        <IconButton aria-label="delete" onClick={(e)=>{handleDelete(e, params.row.id);}}>
          <Delete />
        </IconButton>
      </div>)
  }
];
var handleEdit=(e, contact)=>{
  e.preventDefault();
  setSelectedID(contact.id);
  onEdit(selectedID, contact);
}
var handleDelete=(e, id)=>{
  e.preventDefault();
  setSelectedID(id);
  onDelete(id);
}
  
  return (
    <div className="App">
      <Box sx={{ height: 400, width: '100%' }}>
      <DataGrid
        rows={contacts}
        columns={columns}
        initialState={{
          pagination: {
            paginationModel: {
              pageSize: 15,
            },
          },
        }}
        pageSizeOptions={[15]}
      />
    </Box>
    </div>
  );
}

export default Grid;
